import React, { Component, Fragment} from 'react';
import { Link } from "react-router-dom";
import '../UI/Navbar.css'
import logo from "../../assets/logo.png";
import NavMenu from './Nav/Nav-Menu';
import seal from '../../assets/Seal.png'


import { List, X } from 'react-bootstrap-icons';

class Navbar extends Component {

    state = {
        showMobileNavMenu: false
    }

    toggleMobileMenu = () => {
        this.setState({showMobileNavMenu: !this.state.showMobileNavMenu})
        console.log(this.state.showMobileNavMenu)
    }

    render () {
        let officeRoutes = [{title: 'Meet Your DA', route: '/meet-your-da'}, {title: 'Our Mission', route: '/mission'}, {title: 'Management Team', route: '/management-team'}, {title: 'Our Duties', route: '/duties-responsibilities'}, {title: 'Budget', route: '/budget'}, {title: 'Press', route: '/press'}, {title: 'Careers', route: '/careers'}, {title: 'Externship', route: '/externship'}, {title: `DA Speaker's Bureau`, route: '/da-speaker-bureau'}, {title: `DA Statistics`, route: '/stats'}];
        let prosecutionRoutes = [{title: 'Criminal Complaints', route: '/criminal-complaints'}, {title: 'Domestic Violence', route: '/domestic-violence'}, {title: 'Elder Abuse', route: '/elder-abuse'}, {title: 'Gang Unit', route: '/gang-unit'}, {title: 'Narcotic Unit', route: '/narcotic-unit'}, {title: 'Real Estate', route: '/real-estate'}, {title: 'Rural Crime', route: '/rural-crime'}, {title: 'Sexual Assault', route: '/sexual-assault'}, {title: `Identity Theft`, route: '/identity-theft'}, {title: `Hate Crimes`, route: '/hate-crimes'}, {title: `Diversion Program`, route: '/diversion-program'}];
        let investigationRoutes = [{title: 'Prosecution Support', route: '/prosecution-support'}, {title: 'Welfare Fraud', route: '/welfare-fraud'}, {title: `Worker's Comp. Fraud`, route: '/workers-compensation-fraud'}];
        let vwRoutes = [{title: 'Victim Witness Unit', route: '/victim-witness-unit'}, {title: 'Child Abuse', route: '/child-abuse'}, {title: 'Victim Compensation', route: '/victim-compensation'}, {title: 'Crime Victim Assistance', route: '/crime-victim-assistance'}, {title: 'Domestic Violence', route: '/vwdv'}, {title: 'Sexual Assault', route: '/vw-sexual-assault'}];
        let faqRoutes = [{title: 'FAQs', route: '/faqs'}, {title: 'Criminal Justice System', route: '/criminal-justice-system'}, {title: 'Publications', route: '/publications'}, {title: 'Legal Links', route: '/legal-links'}];
        let contactRoutes = [{title: 'Contact Info', route: '/contact'}];
        let civilRoutes = [{title: 'Consumer Mediation', route: '/consumer-mediation'}, {title: 'Consumer Protection', route: '/consumer-protection'}, {title: 'Consumer Tips', route: '/consumer-tips'}, {title: 'Environmental Law', route: '/environmental-law'}, {title: 'Legal Resources', route: '/legal-resources'},];
        
        return ( 
            <Fragment>
                <div className="relative">
                    <div className="mx-auto flex flex-col lg:flex-row p-4 justify-between items-center lg:items-end max-w-screen-2xl">
                        <div className="flex flex-row items-end">
                            <Link className="hover:text-mustard" to="/"><img className="mr-4" src={seal} style={{width: '80px'}} alt="DA Logo"/></Link>
                            <div className="font-display text-blue-primary font-medium text-lg mb-4 ">
                                <Link className="hover:text-mustard" to="/">Santa Barbara County District Attorney</Link>
                            </div>
                        </div>
                            
                        <div className="ml-4 hidden md:flex border-b-2 border-mustard mb-3">
                            <NavMenu routes={officeRoutes} isMobile={false} title="Office"></NavMenu>
                            <NavMenu routes={prosecutionRoutes} isMobile={false} title="Prosecution"></NavMenu>
                            <NavMenu routes={civilRoutes} isMobile={false} title="Civil"></NavMenu>
                            <NavMenu routes={investigationRoutes} isMobile={false} title="Investigation"></NavMenu>
                            <NavMenu routes={vwRoutes} isMobile={false} title="Victim Services"></NavMenu>
                            {/* <NavMenu routes={faqRoutes} isMobile={false} title="FAQs"></NavMenu> */}
                            <NavMenu routes={contactRoutes} isMobile={false} title="Contact"></NavMenu>
                        </div>

                        <div id="google_translate_element"></div>

                        <div class="md:hidden flex items-center">
                            <button className="outline-none" onClick={() => {this.toggleMobileMenu()} }>
                                {
                                    this.state.showMobileNavMenu ? 
                                    <X className="outline-none" size={24} />
                                    :
                                    <List className="outline-none" size={24} />
                                }
                            
                            </button>
                        </div>
                    </div>

                    <div className="md:hidden">
                        <div className="p-4 shadow absolute flex flex-column justify-center bg-white right-0" style={{display: this.state.showMobileNavMenu ? 'flex' : 'none'}}>
                            <NavMenu routes={officeRoutes} isMobile={true} title="Office"></NavMenu>
                            <NavMenu routes={prosecutionRoutes} isMobile={true} title="Prosecution"></NavMenu>
                            <NavMenu routes={investigationRoutes} isMobile={true} title="Investigation"></NavMenu>
                            <NavMenu routes={vwRoutes} isMobile={true} title="Victim Services"></NavMenu>
                            <NavMenu routes={faqRoutes} isMobile={true} title="FAQs"></NavMenu>
                            <NavMenu routes={contactRoutes} isMobile={true} title="Contact"></NavMenu>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default Navbar;
